import React, { useContext } from 'react'
import AuthContext from '../context/AuthContext'

const Navbar = () => {

 const {user, dispatch}= useContext(AuthContext);

 const logoutHandler=()=>{
    console.log("Logout called");
    try{
    localStorage.removeItem("user");
    }
    catch(err)
    {
        console.log(err);
    }
    dispatch({type:"l"})

 }


  return (
    <div className='row bg-light' style={{width: "match-parent", height:"5rem"}}>
    <div className='col-4 p-4' style={{fontSize: "1.5rem"}}>
    {!user.name ? "YOUR" : user.name + "'s"} CLASSROOM
    </div>
    <div className='col-4'></div>
    <div className='col-4 p-4 mr-2' style={{textAlignLast: "right" , fontSize: "1.5rem"}}>
    <span onClick= {logoutHandler}>LOGOUT</span>
    </div>
    </div>
  )
}

export default Navbar
