import React from 'react'
import Navbar from '../components/Navbar'
import {useState, useEffect, useContext} from 'react'
import AuthContext from '../context/AuthContext'
import axios from 'axios'
import ClassroomCard from '../components/ClassroomCard'
import { toast, ToastContainer } from 'react-toastify'
import {Modal} from 'antd'

const Classrooms = () => {

  const {user}= useContext(AuthContext);
  const [classroomList, setClassroomList]= useState([]);
  const [newClass, setNewClass]= useState("");
  const [showmodal , setShowmodal]= useState(false);

  useEffect(()=>{
    const fetchClasses= async ()=>{
        try{
        console.log(user._id);
        const res= await axios.get(`http://localhost:5500/api/classroom/${user._id}`);
        console.log(res.data)
        setClassroomList(res.data);
        }
        catch(err)
        {
            console.log(err);
        }
    }

    fetchClasses();
    console.log(classroomList.length);
  }, [])

  const addClassroomHandler=async()=>{

    try{
        const {data}= await axios.post('http://localhost:5500/api/classroom/', {
            classroomName: newClass,
            teacherId: user._id
        });

        setClassroomList([...classroomList, {
            ...data
        }])


    }
    catch(err)
    {
        console.log(err);
        toast("Couldn't add classroom. Try again please.")
    }

    setNewClass("");

  }


  return (
    <>
     <div>
        <ToastContainer
        position="top-center"
        autoClose={10000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover/>
        </div>
    <Navbar />
    <div className='container-fluid' >
    <button onClick={()=>{showModal(true)}}>ADD CLASSROOM</button>
    <Modal title="Basic Modal" open={showmodal} onOk={addClassroomHandler} onCancel={()=>{showModal(false)}}>
    <div className='container row bg-light m-2  mt-4 p-4 pt-5 justify-content-center' style={{"width":"40%"}}>
    <div>
    <input type="text" className='form-control'  style= {{fontSize:"1.2rem", borderColor:"orange", borderWidth:"2px"}} value={newClass} onChange={(e)=> setNewClass(e.target.value)} placeholder="Add classroom name" />
    </div>
    <div className='d-flex flex-row-reverse'>
    <div className='p-2 m-2' style={{width:"fit-content", fontSize:"1.2rem", backgroundColor:"orange", color: "white", fontWeight:"600", borderRadius:"10px"}} onClick={addClassroomHandler}>ADD CLASSROOM</div>
    </div>
    </div> 
    </Modal>
    {/* */}

    {
        // try generating custom images from replicate 

        classroomList.length ==0 ? 
        <div className='m-3 p-3 ' style={{fontSize: "3rem", color: "#dddddd", textAlign:"center", fontWeight:"600"}}>No Classroom To Show </div> :
        <div style={{display:"flex", flexWrap:"wrap", height:"match-parent", width:"match-parent", alignItems:"center"}}>
        {
            classroomList.map((myclass)=>{
                return <ClassroomCard id={myclass._id} name={myclass.classroomName} />
            })
        }
        </div>

    }
    </div>
    </>
  )
}

export default Classrooms
