import Register from "./Register"
import { useState } from "react";
import './Auth.css'
import { ToastContainer, toast } from 'react-toastify';
const Auth =()=> {

    let login= useState(true);

    return (
      <div className="container-fluid " style={{"min-height": "100vh"}}>
       <ToastContainer />
      <div className="row" style={{"min-height": "100vh"}}>
        <div className="col p-2">
        <img src="/empty-room.png" class="img-fluid" style={{"min-height": "100vh","border-radius": "25px"}}/>
        </div>
        <div className="col bg-light p-6">
        <div className=""></div>
        <div className= "row p-3 p-5 mb-5 lead "><p className="h1 heading">ATTENDANCE MONITOR</p></div>
        <div className="row bg-white py-5 m-3">
        {

            login ? 
            <div>
            <Login /> 

            </div>


            : <Register />

        }
        </div>

        </div>
        </div>
      </div>
    );
  }

  export default Auth;
