import React, { useContext } from 'react'
import AuthContext from '../context/AuthContext'

const Navbar = () => {

 const {user, dispatch}= useContext(AuthContext);

 const logoutHandler=()=>{
    console.log("Logout called");
    try{
    localStorage.removeItem("user");
    }
    catch(err)
    {
        console.log(err);
    }
    dispatch({type:"LOGOUT"});

 }


  return (
    <div className='row bg-light' style={{width: "match-parent"}}>
    <div className= "col-4 p-4 lead"><p className="h1 heading">attend_it</p></div>
    <div className='col-4 p-5' style={{fontSize: "2rem", fontWeight:'600', color:"orange", fontFamily:"Raleway"}}>
    {!user.teachername ? "YOUR" : user.teachername + "'s"} Classroom
    </div>
    <div className='col-4 p-4 mr-2' style={{textAlignLast: "right" , fontSize: "1.5rem"}}>
    <span className='pr-3' onClick= {logoutHandler} style={{fontSize: "2rem", fontWeight:'600', color:"orange", fontFamily:"Raleway"}}>LOGOUT</span>
    </div>
    </div>
  )
}

export default Navbar
