import React from 'react'
import Navbar from '../components/Navbar'
import {useState, useEffect, useContext} from 'react'
import AuthContext from '../context/AuthContext'
import axios from 'axios'
import ClassroomCard from '../components/ClassroomCard'
const Classrooms = () => {

  const {user}= useContext(AuthContext);
  const [classroomList, setClassroomList]= useState([]);

  useEffect(()=>{
    const fetchClasses= async ()=>{
        try{
        console.log(user._id);
        const res= await axios.get(`http://localhost:5500/api/classroom/${user._id}`);
        console.log(res.data)
        setClassroomList(res.data);
        }
        catch(err)
        {
            console.log(err);
        }
    }

    fetchClasses();
    console.log(classroomList.length);
  }, [])
  return (
    <>
    <Navbar />
    <div className='container-fluid'>
    {
        classroomList.length ==0 ? 
        <div>No Classroom To Show </div> :
        <div className='container-fluid d-flex flex-wrap align-items-center' style={{height:"match-parent"}}>
        {
            classroomList.map((myclass)=>{
                return <ClassroomCard id={myclass._id} name={myclass.name} />
            })
        }
        </div>

    }
    </div>
    </>
  )
}

export default Classrooms
