import Login from "./Login";
import Register from "./Register"
import { useState } from "react";
import './Auth.css'
const Auth =()=> {

    let login= useState(true);

    return (
      <div className="container-fluid " style={{"min-height": "100vh"}}>
      <div className="row" style={{"min-height": "100vh"}}>
        <div className="col p-2">
        <img src="/empty-room.png" class="img-fluid" style={{"min-height": "100vh","border-radius": "25px"}}/>
        </div>
        <div className="col bg-light p-6">
        <div className= "row p-3 lead "><p className="h2">ATTENDANCE ME</p></div>
        <div className="row">
        {

            login ? 
            <Login /> : <Register />

        }
        </div>
        </div>
        </div>
      </div>
    );
  }

  export default Auth;
