import { useState, useEffect } from "react";
import axios from 'axios'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Register =()=> {

    let [name, setName]= useState("");
    let [email, setEmail]= useState("");
    let [password, setPassword]= useState("");

    const onSubmitHandler= async (e)=>{

        try{
            e.preventDefault();

            const res= await axios.post('http://localhost:5500/api/auth/register', {
                teachername: name,
                email: email,
                password: password
            });

            console.log(res);
           toast("Registered successfully");
           // now route to the login page
        }
        catch(err)
        {
            console.log(err);
            toast("Username or Password is not correct");

        }


        }

  return (
    <>

        <div class="container">
        <div class="row justify-content-center align-items-center">
          <form class="row col-12"> 
        <div className="form-group">
          <label for="exampleName">Name</label>
          <input type="text" className="form-control" id="exampleName" placeholder="Enter Name"  
          onChange={(e)=>{setName(e.target.value)}}
          />
          <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div className="form-group">
          <label for="exampleInputEmail1">Email address</label>
          <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"  
          onChange={(e)=>{setEmail(e.target.value)}}
          />
          <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1">Password</label>
          <input type="password" className="form-control" id="exampleInputPassword1" placeholder="Password"
          onChange={(e)=>{setPassword(e.target.value)}} />
        </div>
        <div class="form-group pt-2">
        <button type="submit" className="btn btn-primary" onClick={onSubmitHandler}>Submit</button>
        </div>
        </form>
        </div>
        </div>

        </>
  );
}

export default Register;
