import React, { useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useState } from 'react';

const ClassroomCard = ({id, name}) => {

  const navigate= useNavigate();
  const [imgurl, setImgurl]= useState("");
  const classroomSelectHandler= ()=>{
    console.log("classroom clicked"+ id);
    navigate(`/class/${id}`);
  }

  useEffect(()=>{
    fetchImge();
  },[])
  const fetchImge= async ()=>{
    try{
        const {data}= await axios.get(`https://api.pexels.com/v1/search?query=${name}`, {
          headers:{
            'Authorization': 'CuXr5jWrQEh38a4vgm8n7QsVeUD3vm7Z1f47sbvdrnzaBtVNmuJJgCGm',
            'Content-Type': 'application/json'
          }
        });

        setImgurl(data.photos[0].src.original);

    }
    catch(err){

    }
  }
    // to add custom images from replicate
  return (
    <div className="m-3 p-0" onClick={classroomSelectHandler} style={{border:"2px solid orange" , color: "orange", fontSize:"3rem", backgroundColor:"whitesmoke", width:"30%", maxWidth:"30%",  height:"60%", maxHeight:"60%", borderRadius:"20px", textAlign:"center"  }}> 
    <img src= {imgurl} style={{width:"100%" , height:"100%", objectFit: "cover", overflow: "hidden", borderRadius:"20px"}} />
    <p> style={{zindex:"100", color:"white" }} >{name}</p>
    </div>
  )
}

export default ClassroomCard
