import React from 'react'
import { useNavigate } from 'react-router-dom';

const ClassroomCard = ({id, name}) => {

  const navigate= useNavigate();
  const classroomSelectHandler= ()=>{
    console.log("classroom clicked"+ id);
    navigate(`/class/${id}`);
  }
    // to add custom images from replicate
  return (
    <div className="m-3 p-5" onClick={classroomSelectHandler} style={{color: "orange", fontSize:"3rem", backgroundColor:"white", width:"30%", maxWidth:"30%",  height:"60%", maxHeight:"60%", borderRadius:"20px"}}> 
    {name}
    </div>
  )
}

export default ClassroomCard
