import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import ClassCard from '../components/ClassCard';
const Class = () => {
  const [classes, setClasses]= useState([]);
  const {id}= useParams();

  useEffect(()=>{
    console.log("called");
    const fetchClass= async ()=>{
        try{
            console.log("called2")
            const {data}= await axios.get(`http://localhost:5500/api/classroom/classes/${id}`);
            console.log(data);
            setClasses(data);
        }
        catch(err){
            console.log(err);
        }
    }

    fetchClass();
    console.log(classes.length);
  }, [])



  return (
    <>
    <div>
    {
        classes.length ==0 ? 
        <div>No classes to show</div> 
        :
        <div className='container'>
        {classes.map((item)=>{return <ClassCard id={item ._id} name={item.name} />})}
        </div>



    }
    </div>
    </>
  )
}

export default Class
