import './Response.css'
const Response = ({name, rollno, classLocation, responseLocation}) => {

  const lat1= parseFloat(classLocation.latitude).toFixed(4);
  const long1= parseFloat(classLocation.longitude).toFixed(4);
  const lat2= parseFloat(responseLocation.latitude)
  return (
    <div className='container row p-3 m-2 box-shadow-3d' style={{borderRadius: "4px"}}>
    <div className='col'>{name}</div>
    <div className='col'>{rollno}</div>
    <div>{classLocation.latitude + " "+  classLocation.longitude } + {responseLocation.latitude + " " +  responseLocation.longitude}</div>
