    const [showQR, setShowQR]= useState(false);

    const getQRImageHandler= async()=>{

      try{
        const {data}= axios.get('https://api.beaconstac.com/api/2.0/qrcodes/', {
          headers:{
            "Authorization": 'Token de3d2b9ed0c67e80276417617290ba32675ad4f1'
          },
          data:{
              "name": "Custom URL",
              "organization": 486961,
              "qr_type": 2,
              "campaign": {
                  "content_type": 1,
                  "custom_url": "https://www.pinterest.com"
              },
              "location_enabled": true,
              "attributes":{
                  "color":"#2595ff",
                  "colorDark":"#2595ff",
                  "margin":80,
                  "isVCard":false,
                  "frameText":"BEACONSTAC",
                  "logoImage":"https://d1bqobzsowu5wu.cloudfront.net/15406/36caec11f02d460aad0604fa26799c50",
                  "logoScale":0.1992,
                  "frameColor":"#2595FF",
                  "frameStyle":"banner-bottom",
                  "logoMargin":10,
                  "dataPattern":"square",
                  "eyeBallShape":"circle",
                  "gradientType":"none",
                  "eyeFrameColor":"#2595FF",
                  "eyeFrameShape":"rounded"
          }
          }

        });

        const imgurl= await fetchImage(data.results.id);
        setShowQR

        return imgurl;
      }
      catch(err)
      {
         console.log(err);
      }

    }

  return (
    <div className='container row bg-light p-3' style={{width: "match-parent"}}>
      <div className='row'>
      <div className='col' style={{fontSize: "1.5rem", width:"fit-content"}}>{name}</div>
      <div className='col' style={{width:"fit-content"}}><button onClick={getQRImageHandler} className='btn btn-primary' style={{backgroundColor:"orange"}}>SHOW QR</button></div>
      <div className='col' style={{width:"fit-content"}}><button className='btn btn-primary' style={{backgroundColor:"orange"}}>SHOW RESPONSES</button></div>
      </div>
    </div>
  )
}

export default ClassCard
