import React from 'react'
import axios from 'axios'
import { useState } from 'react';
import { toast , ToastContainer} from 'react-toastify';

const ClassCard = ({id, name}) => {

    // can add generate pdf report of responses also if want to ->FEATURE ADDITION -> CAN BE SEEN AT END]


    // issi mei qr generate krna hoga
    // make a small list type component -> onclick it opens a modal with a qr that has that id
    /* ON THE CARD WE HAVE OPTION OF SHOW QR AND SHOW RESPONSES */

    const [showQR, setShowQR]= useState(false);
    const [qrurl, setQrurl]= useState("");

    const getQRImageHandler= async()=>{

      try{
        console.log("handler called");
        const {data}= await axios.post('https://api.beaconstac.com/api/2.0/qrcodes/', {
          headers:{
            "Authorization": 'Token de3d2b9ed0c67e80276417617290ba32675ad4f1'
          },
          data:{
              "name": "MYURL",
              "organization": 486961,
              "qr_type": 2,
              "campaign": {
                  "content_type": 1,
                  "custom_url": "https://www.pinterest.com"
              },
              "location_enabled": true,
              "attributes":{
                  "color":"#2595ff",
                  "colorDark":"#2595ff",
                  "margin":80,
                  "isVCard":false,
                  "frameText":"BEACONSTAC",
                  "logoImage":"https://d1bqobzsowu5wu.cloudfront.net/15406/36caec11f02d460aad0604fa26799c50",
                  "logoScale":0.1992,
                  "frameColor":"#2595FF",
                  "frameStyle":"banner-bottom",
                  "logoMargin":10,
                  "dataPattern":"square",
                  "eyeBallShape":"circle",
                  "gradientType":"none",
                  "eyeFrameColor":"#2595FF",
                  "eyeFrameShape":"rounded"
          }
          }

        });
        console.log(data);
        if(data && data.results)
        {
        const imgurl= await fetchQRImage(data.results.id);
        setQrurl(imgurl);
        setShowQR(true);
        }

      }
      catch(err)
      {

         console.log(err);
         toast("Failed to load Q")
      }

    }

    const fetchQRImage= async(id)=>{
      try{
         const {data}= await axios.get(`https://api.beaconstac.com/api/2.0/qrcodes/${id}/download/?size=1024&error_correction_level=5&canvas_type=png/`, {
          headers:{
            "Authorization": 'Token de3d2b9ed0c67e80276417617290ba32675ad4f1'
          }
         });
         return data.urls.png;
        }
      catch(err)
      {
        console.log(err);
        toast("Failed to load QR");
      }
    }

  return (
    <>
        <ToastContainer
        position="top-center"
        autoClose={10000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover/>
    <div className='container row bg-light p-3' style={{width: "match-parent"}}>
      <div className='row'>
      <div className='col' style={{fontSize: "1.5rem", width:"fit-content"}}>{name}</div>
      <div className='col' style={{width:"fit-content"}}><button onClick={getQRImageHandler} className='btn btn-primary' style={{backgroundColor:"orange"}}>SHOW QR</button></div>
      <div className='col' style={{width:"fit-content"}}><button className='btn btn-primary' style={{backgroundColor:"orange"}}>SHOW RESPONSES</button></div>
      {
        showQR && <div><img src={qrurl} /> HEY</div>
      }
      </div>
    </div>
    </>
  )
}

export default ClassCard
