import React from 'react'
import axios from 'axios'
import { useState } from 'react';
import { toast , ToastContainer} from 'react-toastify';
import Response from './Response';

const ClassCard = ({id, name}) => {

    // can add generate pdf report of responses also if want to ->FEATURE ADDITION -> CAN BE SEEN AT END]


    // issi mei qr generate krna hoga
    // make a small list type component -> onclick it opens a modal with a qr that has that id
    /* ON THE CARD WE HAVE OPTION OF SHOW QR AND SHOW RESPONSES */
    const [show, setShow]= useState("");
    const [qrurl, setQrurl]= useState("");
    const [responses, setResponses]= useState([]);
    const [imgurl, setImgurl]= useState("");
    let axiosConfig = {
      headers:{
        'Authorization': 'Token 57c4d555b72e48bc12921ae87c96b2977018ba16', 
        'Content-Type': 'application/json'
      }
    };

    const fetchImge= async ()=>{
      try{
          const {data}= await axios.get(`http://api.pexels.com/v1/search?query=${name}`, {
            headers:{
              Authorization: 'CuXr5jWrQEh38a4vgm8n7QsVeUD3vm7Z1f47sbvdrnzaBtVNmuJJgCGm'
            }
          });

          setImgurl(data.src[0].original);

      }
      catch(err){

      }
    }



    const getQRImageHandler= async()=>{

      try{
        console.log("handler called");
        const {data}= await axios.post('https://q.api.beaconstac.com/api/2.0/qrcodes/', {
            name: "Mongodb",
            organization: 24595,
            qr_type: 2,
            campaign: {
              content_type: 1,
              custom_url:`https://7068-14-143-179-154.ngrok-free.app/form/${id}`
            },
            location_enabled: false,
            attributes: {
              color: "#2595ff",
              colorDark: "#2595ff",
              margin: 80,
              isVCard: false,
              frameText: "BEACONSTAC",
              logoImage: "https://d1bqobzsowu5wu.cloudfront.net/15406/36caec11f02d460aad0604fa26799c50",
              logoScale: 0.1992,
              frameColor: "#2595FF",
              frameStyle: "banner-bottom",
              logoMargin: 10,
              dataPattern: "square",
              eyeBallShape: "circle",
              gradientType: "none",
              eyeFrameColor: "#2595FF",
              eyeFrameShape: "rounded"
            }
          }, axiosConfig

        );


        console.log(data);
        if(data)
        {
        const imgurl= await fetchQRImage(data.id);
          if(imgurl)
          {
          setQrurl(imgurl);
          setShow("qr");
          }
        }

      }

      catch(err)
      {

         console.log(err);
         toast("Failed to load QR");
      }

    }

    const responsesHandler= async ()=>{

      try{
        const {data}= await axios.get(`http://localhost:5500/api/classroom/get-responses/${id}`)
        setResponses(data);
        setShow("responses");
      }
      catch(err)
      {
        console.log(err);
      }

    }
    const fetchQRImage= async(id)=>{
      try{
        console.log(id);
         const {data}= await axios.get(`https://q.api.beaconstac.com/api/2.0/qrcodes/${id}/download/?size=1024&error_correction_level=5&canvas_type=png`, {
          headers:{
            'Authorization': 'Token 57c4d555b72e48bc12921ae87c96b2977018ba16', 
            'Content-Type': 'application/json'
          },
         });
         console.log(data);
         return data.urls.png;
        }
      catch(err)
      {
        console.log(err);
        toast("Failed to load QR");
      }
    }

  return (
    <>
        <ToastContainer
        position="top-center"
        autoClose={10000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover/>
    <div className='container row bg-light p-3 my-2' style={{width: "match-parent"}}>
      <div className='row'>
      <div className='col' style={{fontSize: "1.5rem", width:"fit-content"}}>{name}</div>
      <div className='col' style={{width:"fit-content"}}>
      { show != "qr"?
        <button onClick={getQRImageHandler} className='btn btn-primary' style={{backgroundColor:"orange"}}>SHOW QR</button> :
        <button onClick={()=>setShow("")} className='btn btn-primary' style={{backgroundColor:"whitesmoke", color:"black"}}>HIDE QR</button>
      }
        </div> 
      <div className='col' style={{width:"fit-content"}}>
      {
        show != "responses"?
        <button className='btn btn-primary' style={{backgroundColor:"orange"}} onClick={responsesHandler}>SHOW RESPONSES</button> :
        <button className='btn btn-primary' style={{backgroundColor:"whitesmoke", color:"black"}} onClick={()=>{setShow("")}}>HIDE RESPONSES</button>
      }
      </div>
      {
        show == "qr" && <div className='row pt-3'><img src={qrurl}  style={{width: "40%", height:"80%", margin:"auto"}}/></div>
      }
      {
        show == "responses" && 
        <div className='row'>
        {
          responses.map((item)=>{
            return <Response name= {item.name} rollno= {item.rollno} />
          })
        }
        </div>
      }
      </div>
    </div>
    </>
  )
}

export default ClassCard
