import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import ClassCard from '../components/ClassCard';
import Navbar from '../components/Navbar';
import { toast } from 'react-toastify';
import {Modal} from 'antd'
import {FloatButton} from 'antd'
import './Classroom.css'
import {PlusOutlined} from '@ant-design/icons'

const Class = () => {
  const [classes, setClasses]= useState([]);
  const [className, setClassName]= useState("");
  const [newroom, setNewroom]= useState("");
  const {id}= useParams();

  useEffect(()=>{
    console.log("called");
    const fetchClass= async ()=>{
        try{
            const {data}= await axios.get(`http://localhost:5500/api/classroom/classes/${id}`);
            console.log(data);
            setClassName(data.name);
            setClasses(data.classes);
        }
        catch(err){
            console.log(err);
        }
    }

    fetchClass();
    console.log(classes.length);
  }, [])

  const roomAddHandler= async()=>{
      try{
          const {data}= await axios.post(`http://localhost:5500/api/classroom/add-class/${id}`, {
            className: newroom
          });
          setClasses([...classes, data]);
          setNewroom("");
      }
      catch(err){
          console.log(err);
          toast("Couldn't create new room. Try Again")
      }
  }

  return (
    <>
    <Navbar/>
    <FloatButton style={{backgroundColor:"orange", color:"white", fontWeight:"700", width:"5rem", height:"5rem"}}icon= <PlusOutlined style={{fontSize:"1.5rem", fontWeight:"700"}}/>onClick={()=>setShowmodal(true)}/>
    <Modal centered okButtonProps={{ style: { backgroundColor: 'orange'} }}  style={{backgroundColor: "grey" , borderRadius:"20px"}} title="ADD NEW CLASSROOM" open={showmodal} onOk={addClassroomHandler} onCancel={()=>{setShowmodal(false)}}>

    <input type="text" className='form-control'  style= {{fontSize:"1.2rem", borderColor:"orange", borderWidth:"2px"}} value={newClass} onChange={(e)=> setNewClass(e.target.value)} placeholder="Add classroom name" />
    </Modal>
    <div className='container'>
    <div className='row m-5'></div>
    <div className='pt-5 pb-3' style={{fontSize: "2rem"}}>{className}</div>
    <div className='row p-4 container' style={{border:"1px solid black" , borderRadius:"10px"}}>
    <div className='col-8'>
    <input type="text" value={newroom} onChange={(e)=>{setNewroom(e.target.value)}}style={{fontSize:"1.2rem"}} placeholder='ADD NEW ROOM' className='form-control'/>
    </div>
    <div className='col-4'>
    <button className='btn btn-primary' onClick={roomAddHandler} style={{backgroundColor:"orange", fontSize:"1.2rem"}} >SUBMIT</button>
    </div>
    </div>

    <div>
    {
        classes.length ==0 ? 
        <div>No classes to show</div> 
        :

        classes.map((item)=>{return <ClassCard id={item ._id} name={item.name} />})

    }
    </div>
    </div>
    </>
  )
}

export default Class
