import React from 'react'
import Navbar from '../components/Navbar'
import {useState, useEffect, useContext} from 'react'
import AuthContext from '../context/AuthContext'
import axios from 'axios'
import ClassroomCard from '../components/ClassroomCard'
const Classrooms = () => {

  const {user}= useContext(AuthContext);
  const [classroomList, setClassroomList]= useState([]);
  const [newClass, setNewClass]= useState("");

  useEffect(()=>{
    const fetchClasses= async ()=>{
        try{
        console.log(user._id);
        const res= await axios.get(`http://localhost:5500/api/classroom/${user._id}`);
        console.log(res.data)
        setClassroomList(res.data);
        }
        catch(err)
        {
            console.log(err);
        }
    }

    fetchClasses();
    console.log(classroomList.length);
  }, [])


  return (
    <>
    <Navbar />
    <div className='container-fluid'>
    <div className='container row bg-light m-2  mt-4 p-2'>
    <input type="text" className='form-control' value={newClass} onChange={(e)=> e.target.value}/>
    </div>
    <div  style={{width:"fit-content", fontSize:"1.5rem"}} onClick={addClassroomHandler}>ADD CLASSROOM</div>

    {
        // try generating custom images from replicate 

        classroomList.length ==0 ? 
        <div>No Classroom To Show </div> :
        <div className='d-flex flex-wrap align-items-center row' style={{height:"match-parent", width:"match-parent"}}>
        {
            classroomList.map((myclass)=>{
                return <ClassroomCard id={myclass._id} name={myclass.classroomName} />
            })
        }
        </div>

    }
    </div>
    </>
  )
}

export default Classrooms
