import React from 'react'
import './Response.css'
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
const Response = ({name, rollno, classLocation, responseLocation}) => {

  const lat1= parseFloat(classLocation.latitude).toFixed(6);
  const long1= parseFloat(classLocation.longitude).toFixed(6);
  const lat2= parseFloat(responseLocation.latitude).toFixed(6);
  const long2= parseFloat(responseLocation.longitude).toFixed(6);
  return (
    <div className='container row p-3 m-2 box-shadow-3d' style={{borderRadius: "4px"}}>
    <div className='col'>{name}</div>
    <div className='col'>{rollno}</div>
    {/* <div>{classLocation.latitude + " "+  classLocation.longitude } + {responseLocation.latitude + " " +  responseLocation.longitude}</div> */}
    { (lat1 != lat2 || long1 != long2) ?
      <div className='col m-1 p-2' style={{backgroundColor:"red", color:"white", borderRadius:"4px", fontWeight:"700", textAlign:"center"}}>LOCATION MISMATCH</div> :
      <div className='col' style={{backgroundColor:"red", color:"white", borderRadius:"4px", fontWeight:"700", textAlign:"center"}}><CheckCircleIcon style={{backgroundColor:"green"}}/></div> 

    }
    </div>
  )
}

export default Response
