import React, { useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useState } from 'react';

const ClassroomCard = ({id, name}) => {

  const navigate= useNavigate();
  const [imgurl, setImgurl]= useState("");
  const classroomSelectHandler= ()=>{
    console.log("classroom clicked"+ id);
    navigate(`/class/${id}`);
  }

  useEffect(()=>{
    fetchImge();
  },[])
  const fetchImge= async ()=>{
    try{
        const {data}= await axios.get(`http://api.pexels.com/v1/search?query=${name}`, {
          headers:{
            Authorization: 'CuXr5jWrQEh38a4vgm8n7QsVeUD3vm7Z1f47sbvdrnzaBtVNmuJJgCGm',
            'Content-Type': 'application/json',
      "Access-Control-Allow-Origin": "*",
          }
        });

        setImgurl(data.src[0].original);

    }
    catch(err){

    }
  }
    // to add custom images from replicate
  return (
    <div className="m-3 p-5" onClick={classroomSelectHandler} style={{border:"2px solid orange" , color: "orange", fontSize:"3rem", backgroundColor:"white", width:"30%", maxWidth:"30%",  height:"60%", maxHeight:"60%", borderRadius:"20px", backgroundImage: `url(${imgurl})` }}> 
    {name}
    </div>
  )
}

export default ClassroomCard
